import React, { useEffect, useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fectchStarShips();
  }, []);
  // if we use fectchStarShips as a dependency we will fall into loop as, this function changes whenever this any of the state in this fucntion changes.
  // so we are supposed to use useCallback()
  async function fectchStarShips() {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch("https://swapi.dev/api/films/");
      const data = await response.json();
      // response.ok
      // response.ok which basically tells whether the response was succesfull or not.
      // response.status which holds the response status code
      if (!response.ok) {
        throw new Error("something went wrong!!");
      }
      const transformedData = data.results.map((movieData) => {
        return {
          id: movieData.episode_id,
          title: movieData.title,
          openingText: movieData.opening_crawl,
          releaseDate: movieData.release_date,
        };
      });
      setMovies(transformedData);
    } catch {
      setError(error.message);
    }
    setIsLoading(false);
  }

  let content = <p>found no movies</p>;
  if (movies.length > 0) {
    content = <MoviesList movies={movies} />;
  }
  if (error) {
    content = <p>{error}</p>;
  }
  if (isLoading) {
    content = <p>Loading !!........!!</p>;
  }

  return (
    <React.Fragment>
      <section>
        <button onClick={fectchStarShips}>Fetch Movies</button>
      </section>
      <section>
        {/* {!isLoading && movies.length > 0 && <MoviesList movies={movies} />}
        {!isLoading && movies.length === 0 && !error && (
          <p>Found no movies!!</p>
        )}
        {!isLoading && error && <p>{error}</p>}
        {isLoading && <p>Loading !!........!!</p>} */}
        {content}
      </section>
    </React.Fragment>
  );
}

export default App;
